SELECT COUNT(*) AS FailureCount
FROM Config.vwIntegrationMetadata
WHERE JOBID = @{pipeline().parameters.JobID}
AND ExecutionTypeNum = @{pipeline().parameters.ExecutionTypeNum}
AND Status = 'Failed'

DECLARE @JobId INT
	,@ExecutionTypeNum INT
	,@Priority INT
	,@ErrorMessage NVARCHAR(MAX)

SET @JobId = @{pipeline().parameters.JobID}
SET @ExecutionTypeNum = @{pipeline().parameters.ExecutionTypeNum}
SET @Priority = @{int(variables('i'))}

SET @ErrorMessage = 'IntegrationTypeOrchestration is failed for JobId:'+CAST(@JobId AS NVARCHAR(10))+',ExecutionTypeNum:'+CAST(@ExecutionTypeNum AS NVARCHAR(10))+',Priority:'+CAST(@Priority AS NVARCHAR(10))

RAISERROR(@ErrorMessage,11,1)

SELECT 1 AS Num

SELECT DISTINCT IntegrationId FROM Config.vwIntegrationMetadata 
WHERE ISExecute = 1 
AND JobID = @{pipeline().parameters.JobId} 
AND ExecutionTypeNum = @{pipeline().parameters.ExecutionTypeNum} 
AND ISNULL([Priority],0)= @{pipeline().parameters.Priority} 
AND [status] IN ('Ready','Failed','Inprogress')
ORDER BY IntegrationId

SELECT * FROM Config.vwIntegrationMetadata 
WHERE JobId = @{pipeline().parameters.JobId}
AND ExecutionTypeNum = @{pipeline().parameters.ExecutionTypeNum}
AND Priority = @{pipeline().parameters.Priority}
AND IntegrationId = @{item().IntegrationId}

SELECT COUNT(*) AS PendingCount FROM Config.vwIntegrationMetadata 
WHERE ISExecute = 1 
AND JobID = @{pipeline().parameters.JobID} 
AND ExecutionTypeNum = @{int(variables('i'))} 
AND [status]='Ready'
